option.value:
{
    order webdav last
}

setups.michelgerding.nl {
    root * /opt/setups

    basicauth /dav/* {
        # Generate hash with: caddy hash-password
        michel $2a$14$REPLACE_WITH_HASHED_PASSWORD
    }

    route /dav/* {
        uri strip_prefix /dav
        webdav {
            root /opt/setups
        }
    }

    # Proxy admin and info page to the setup-sync service
    reverse_proxy /admin/* setup-sync:3000
    reverse_proxy / setup-sync:3000
}
